CREATE TABLE IF NOT EXISTS `blogs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `cid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '分类id',
  `uid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户ID',
  `title` varchar(50) NOT NULL DEFAULT '' COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `description` varchar(255) NOT NULL DEFAULT '' COMMENT '描述',
  `addtime` int(11) NOT NULL COMMENT '添加时间',
  `uptime` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `status` enum('publish','trash','deleted','sketch') NOT NULL DEFAULT 'sketch' COMMENT '状态,publish:发布,trash:垃圾,deleted:已删除,sketch:草稿',
  `image` varchar(255) NOT NULL DEFAULT '' COMMENT '特色图片',
  `tags` varchar(50) NOT NULL DEFAULT '' COMMENT '标签, tag1,tag2...',
  `read` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '阅读数',
  `comment` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '评论',
  `is_private` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '仅自己可见',
  `allow_review` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '是否允许评论',
  `author` varchar(10) NOT NULL DEFAULT '' COMMENT '作者',
  `is_focus` tinyint(4) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `cid` (`cid`),
  KEY `uid` (`uid`),
  KEY `status` (`status`),
  KEY `focus_index` (`is_focus`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=28 ;

