CREATE TABLE IF NOT EXISTS `category` (
  `cid` smallint(6) NOT NULL AUTO_INCREMENT,
  `pid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '父id',
  `name` varchar(15) NOT NULL DEFAULT '' COMMENT '分类名称',
  `uid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户ID',
  `home` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否显示在首页',
  `count` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '统计',
  PRIMARY KEY (`cid`),
  KEY `pid` (`pid`),
  KEY `home` (`home`),
  KEY `uid` (`uid`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='文章分类表' AUTO_INCREMENT=7 ;

